import React, { useState, useEffect } from "react";
import { TextField, Button, Typography, Paper } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
import FileBase from "react-file-base64";

import useStyles from "./styles";
import { createPost, updatePost } from "../../actions/posts";

const Form = ({ currentId, setCurrentId }) => {
  const [postData, setPostData] = useState({
    name: "",
    dob: "",
    location: "",
    team: "",
    gender: "",
    sports: "",
    about: "",
    interests: "",
    profileimg: "",
  });
  const post = useSelector((state) =>
    currentId ? state.posts.find((message) => message._id === currentId) : null
  );
  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    if (post) setPostData(post);
  }, [post]);

  const clear = () => {
    setCurrentId(0);
    setPostData({
      name: "",
      dob: "",
      location: "",
      team: "",
      gender: "",
      sports: "",
      about: "",
      interests: "",
      profileimg: "",
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(postData);

    if (currentId === 0) {
      dispatch(createPost(postData));
      clear();
    } else {
      dispatch(updatePost(currentId, postData));
      clear();
    }
  };

  return (
    <Paper className={classes.paper}>
      <form
        autoComplete="off"
        noValidate
        className={`${classes.root} ${classes.form}`}
        onSubmit={handleSubmit}
      >
        <Typography variant="h6">
          {currentId ? `Editing "${post.name}"` : "Creating a Profile"}
        </Typography>
        <TextField
          name="name"
          variant="outlined"
          label="Name"
          fullWidth
          value={postData.name}
          onChange={(e) => setPostData({ ...postData, name: e.target.value })}
        />
        <TextField
          name="dob"
          variant="outlined"
          label="Date of Birth"
          fullWidth
          value={postData.dob}
          onChange={(e) => setPostData({ ...postData, dob: e.target.value })}
        />
        <TextField
          name="location"
          variant="outlined"
          label="Location"
          fullWidth
          multiline
          rows={4}
          value={postData.location}
          onChange={(e) =>
            setPostData({ ...postData, location: e.target.value })
          }
        />
        <TextField
          name="team"
          variant="outlined"
          label="Team"
          fullWidth
          value={postData.team}
          onChange={(e) =>
            setPostData({ ...postData, team: e.target.value.split(",") })
          }
        />
        <TextField
          name="gender"
          variant="outlined"
          label="Gender"
          fullWidth
          value={postData.gender}
          onChange={(e) => setPostData({ ...postData, gender: e.target.value })}
        />
        <TextField
          name="sports"
          variant="outlined"
          label="Sports (coma seperated)"
          fullWidth
          value={postData.sports}
          onChange={(e) =>
            setPostData({ ...postData, sports: e.target.value.split(",") })
          }
        />
        <TextField
          name="about"
          variant="outlined"
          label="About"
          fullWidth
          value={postData.about}
          onChange={(e) => setPostData({ ...postData, about: e.target.value })}
        />
        <TextField
          name="Interests"
          variant="outlined"
          label="Interest"
          fullWidth
          value={postData.interests}
          onChange={(e) =>
            setPostData({ ...postData, interests: e.target.value })
          }
        />

        <div className={classes.profileimg}>
          {" "}
          Profile Image{" "}
          <FileBase
            type="file"
            multiple={false}
            onDone={({ base64 }) =>
              setPostData({ ...postData, selectedFile: base64 })
            }
          />
        </div>
        <Button
          className={classes.buttonSubmit}
          variant="contained"
          color="primary"
          size="large"
          type="submit"
          fullWidth
        >
          Submit
        </Button>
        <Button
          variant="contained"
          color="secondary"
          size="small"
          onClick={clear}
          fullWidth
        >
          Clear
        </Button>
      </form>
    </Paper>
  );
};

export default Form;
