import React, { useEffect } from "react";
import { TextField, Button, Typography, Paper } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
import { createPost, updatePost } from "../../actions/posts";
import useStyles from "./styles";
// creating functional component ans getting props from app.js and destucturing them
const StepOne = ({ postData, setPostData, prevStep, currentId }) => {
  //creating error state for validation

  const post = useSelector((state) =>
    currentId ? state.posts.find((message) => message._id === currentId) : null
  );
  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    if (post) setPostData(post);
  }, [post]);

  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();
    if (currentId === 0) {
      dispatch(createPost(postData));
      clear();
    } else {
      dispatch(updatePost(currentId, postData));
      clear();
    }
    // checking if value of first name and last name is empty show error else take to step 2
  };

  return (
    <div>
      <Paper className={classes.paper}>
        <form
          autoComplete="off"
          noValidate
          className={`${classes.root} ${classes.form}`}
        >
          <TextField
            name="about"
            variant="outlined"
            label="About"
            fullWidth
            multiline
            rows={4}
            value={postData.about}
            onChange={(e) =>
              setPostData({ ...postData, about: e.target.value })
            }
          />
          <TextField
            name="interests"
            variant="outlined"
            label="Interest"
            fullWidth
            value={postData.interests}
            onChange={(e) =>
              setPostData({ ...postData, interests: e.target.value })
            }
          />

          <TextField
            name="profileimg"
            variant="outlined"
            label="Profile Image"
            fullWidth
            value={postData.profileimg}
            onChange={(e) =>
              setPostData({ ...postData, profileimg: e.target.value })
            }
          />
        </form>
        <div style={{ display: "flex", justifyContent: "space-around" }}>
          <Button
            className={classes.buttonSubmit}
            variant="contained"
            color="primary"
            size="small"
            type="submit"
            onClick={prevStep}
          >
            Previous
          </Button>
          <Button
            className={classes.buttonSubmit}
            variant="contained"
            color="primary"
            size="small"
            type="submit"
            onClick={submitFormData}
          >
            Submit
          </Button>
        </div>
      </Paper>
